const TextC = ({
  children,
  twcss,
  style,
}: {
  twcss?: string
  style?: any
  children: any
}) => (
  <Text style={[tw`text-white ${twcss as string}`, style]}>
    <>{children}</>
  </Text>
)

const fixData = (arr: any) => {
  const dataFixed = []
  let cc = 0
  let cc2 = 4

  const timesLoop = Math.ceil(arr.length / 4)

  for (let i = 0; i < timesLoop; i++) {
    let plo = arr.slice(cc, cc2)
    dataFixed.push(plo)
    cc = cc + 4
    cc2 = cc2 + 4
  }

  return dataFixed
}

