const CardGenders = ({data}: {data: any}) => {
  if (!data) {
    return <Button type='clear' loading />
  }

  return (
    <FlatList
      data={data}
      horizontal
      renderItem={({item}) => {
        return (
          <FlatList
            data={item}
            renderItem={({item}) => (
              <TouchableOpacity key={item.id}>
                <View
                  style={[
                    tw`px-4 py-3 m-2 w-52 rounded-lg border-l-8`,
                    {
                      backgroundColor: 'hsl(0, 0%, 10%)',
                      borderLeftColor: item.color.toLocaleLowerCase(),
                    },
                  ]}>
                  <Text style={tw`text-white`}>{item.gender}</Text>
                </View>
              </TouchableOpacity>
            )}
          />
        )
      }}
    />
  )
}

const CardTrending = ({data}: {data: any}) => {
  if (!data) {
    return <Button type='clear' loading />
  }

  return (
    <FlatList
      data={data}
      horizontal
      renderItem={({item}) => {
        return (
          <FlatList
            data={item}
            renderItem={({item, index}) => (
              <TouchableOpacity key={item.id} style={[tw`mr-5`, {width: 400}]}>
                <View
                  style={tw`flex flex-row mb-3 items-center w-11/12 overflow-hidden`}>
                  <Image
                    style={tw`w-16 h-8 bg-red-400`}
                    source={{uri: item.img}}
                  />
                  <View style={tw`mx-6 mx-9 self-center`}>
                    <TextC>{index + 1}</TextC>
                  </View>
                  <View>
                    <TextC>{item.title}</TextC>
                    <View style={tw`flex flex-row`}>
                      <TextC twcss={'mr-2'}>{item.channel}</TextC>
                      <TextC>{item.views} views</TextC>
                    </View>
                  </View>
                </View>
              </TouchableOpacity>
            )}
          />
        )
      }}
    />
  )
}

